mapper.sql:
select sd.id, sd.created_time createdTime, sd.name, sd.parent_id parentId, sd.sort, sd.status from sys_dept sd where sd.deleted = 0
and sd.name like concat('%', #{name}, '%')
and sd.status = #{status}
and sd.id in ( select dept_id from sys_role_dept where role_id in ( select id from sys_role where code in
#{roleCode}
)
and data_scope_action_type = #{dataScopeActionType}
)
order by sd.sort, sd.created_time desc
