mapper.sql:
select sm.id, sm.created_time createdTime, sm.parent_id parentId, sm.type, sm.name, sm.sort, sm.status, sm.icon, sm.path, sm.method, sm.permission from sys_menu sm where sm.deleted = 0
and sm.name like concat('%', #{name}, '%')
and sm.status = #{status}
and sm.id in ( select menu_id from sys_role_menu where role_id in ( select id from sys_role where code in
#{roleCode}
) )
sm.type = #{type}
order by sort, created_time desc
