mapper.select:
select code from sys_role
where deleted = 0
and id in (
  select role_id from sys_role_user where user_id = #{id}
)

select permission from sys_menu
where deleted = 0
and id in (
  select menu_id from sys_role_menu where role_id in (
    select role_id from sys_role_user where user_id = #{id}
  )
)
